import React from 'react'; /* eslint no-unused-vars: [0] */
import ReactDOM from 'react-dom';
import App from './src/app';

ReactDOM.render(<App />, document.getElementById('app'));

import React from 'react';
import Immutable from 'immutable';
import { createStore } from 'redux';
import hat from 'hat';

// actions
import {
  mute,
  unmute,
  addRock,
  setRocks,
  removeRock,
  updatePitch,
  setMinBattery,
  updateBearing,
  updateBattery,
  updateLocation,
  updateNetworkSpeed
} from './actions';

// reducers
import dashboardApp from './reducers';

// API
import {
  getStats,
  getRocks,
  postRock,
  deleteRock
} from './api';

// mock API
import {
  getPitch,
  getBearing,
  getLocation,
  getBatteryLevel,
  getNetworkSpeed
} from './mock_client';

// components
import Battery from './components/battery';
import MainMap from './components/main_map';
import RockList from './components/rock_list';
import PanControl from './components/pan_control';
import ZoomControl from './components/zoom_control';
import VideoPlayer from './components/video_player';
import SettingsView from './components/settings_view';
import CamerasView from './components/cameras_view';
import NetworkSparkline from './components/network_sparkline';
import RockCoordinatesForm from './components/rock_coordinates_form';
import BearingPitchRollVisualization from './components/bearing_pitch_roll_visualization';
import BatterySparkline from './components/battery_sparkline';

const POLL_INTERVAL = 3000; // milliseconds to wait between polling vehicles

const BIG_DADDY = 'bigDaddy';
const SCOUT = 'scout';
const FLYER = 'flyer';
const CAMERAS = 'cameras';
const SETTINGS = 'settings';
const vehicles = [ BIG_DADDY, SCOUT, FLYER ];

var store = createStore(dashboardApp, Immutable.fromJS({
  bigDaddy: {
    batteryLevel: 100,
    batteryLevelHistory: [0, 100],
    location: [0, 0],
    networkSpeed: [0],
    color: '#00ff00',
    cameras: [],
    pitch: [0, 0, 0]
  },
  scout: {
    batteryLevel: 100,
    batteryLevelHistory: [0, 100],
    location: [0, 0],
    networkSpeed: [0],
    color: '#ff00ff',
    cameras: [],
    pitch: [0, 0, 0]
  },
  flyer: {
    batteryLevel: 100,
    batteryLevelHistory: [0, 100],
    location: [0, 0],
    networkSpeed: [0],
    color: '#ffff00',
    cameras: [],
    pitch: [0, 0, 0]
  },
  rocks: [],
  video: {
    [BIG_DADDY]: {
      camera1: ''
    },
    [SCOUT]: {
      camera1: ''
    },
    [FLYER]: {
      camera1: ''
    }
  },
  muted: false,
  minBattery: 20
}));

function updateFromServer() {
  getStats()
    .then(stats => {
      for (var vehicle in stats) {
        store.dispatch(updateBattery({
          vehicle,
          batteryLevel: stats[vehicle].batteryLevel
        }));
        store.dispatch(updateLocation({
          vehicle,
          location: stats[vehicle].location
        }));
        store.dispatch(updateNetworkSpeed({
          vehicle,
          data: stats[vehicle].networkSpeed
        }));
        store.dispatch(updateBearing({
          vehicle,
          bearing: stats[vehicle].bearing
        }));
        store.dispatch(updatePitch({
          vehicle,
          pitch: stats[vehicle].pitch
        }));
      }
    })
    .catch(e => console.log(e));

  getRocks()
    .then(rocks => {
      store.dispatch(setRocks(rocks));
    });
}

function mock() {
  for (var i = 0; i < vehicles.length; i++) {
    let vehicle = vehicles[i];
    store.dispatch(updateBattery({
      vehicle,
      batteryLevel: getBatteryLevel(vehicle)
    }));
    store.dispatch(updateLocation({
      vehicle,
      location: getLocation(vehicle)
    }));
    store.dispatch(updateNetworkSpeed({
      vehicle,
      data: getNetworkSpeed(vehicle)
    }));
    store.dispatch(updateBearing({
      vehicle,
      bearing: getBearing(vehicle)
    }));
    store.dispatch(updatePitch({
      vehicle,
      pitch: getPitch(vehicle)
    }));
  }
}

function updateStats() {
  var isDemo = location.search.split('=')[1] === 'true';
  if (!isDemo) {
    updateFromServer();
  } else {
    mock();
  }
}

// poll for battery and location information
window.setInterval(updateStats, POLL_INTERVAL);

const names = {
  [BIG_DADDY]: 'Big Daddy',
  [SCOUT]: 'Scout',
  [FLYER]: 'Flyer',
  [CAMERAS]: 'Cameras',
  [SETTINGS]: 'Settings'
};

const colors = {
  purple: '',
  green: '',
  blue: '',
  red: '',
  orange: '',
  yellow: ''
};

export default class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      data: store.getState(),
      view: BIG_DADDY
    };
  }

  componentDidMount() {
    store.subscribe(() => {
      this.setState({data: store.getState()});
    });
  }

  changeView(view) {
    this.setState({ view });
  }

  getVehicleLocationData() {
    return vehicles.map(v => {
      return {
        vehicle: v,
        coordinates: this.state.data.getIn([v, 'location']).toJS(),
        color: this.state.data.getIn([v, 'color']),
        name: names[v]
      };
    });
  }

  addRock(data) {
    data.id = hat();
    store.dispatch(addRock(data));
    postRock(data);
  }

  removeRock(id) {
    store.dispatch(removeRock(id));
    deleteRock(id);
  }

  setMinBattery(min) {
    store.dispatch(setMinBattery(min));
  }

  render() {
    var data = this.state.data;
    if (vehicles.some(v => v === this.state.view)) {
      var batteryLevel = data.getIn([this.state.view, 'batteryLevel']);
      var batteryLevelHistory = data.getIn([this.state.view, 'batteryLevelHistory']).toJS();
      var networkSpeed = data.getIn([this.state.view, 'networkSpeed']).toJS();
      var vehicleLocations = this.getVehicleLocationData();
      var rockData = data.get('rocks').toJS();
      var pitch = data.getIn([this.state.view, 'pitch']);
    }

    var lowBattery = vehicles.some(v => {
      return data.getIn([v, 'batteryLevel']) < data.get('minBattery');
    });

    return <div>

      {(lowBattery && !data.get('muted')) && <audio preload autoPlay>
        <source src='./lowBattery.mp3' type='audio/mpeg'/>
        Your browser does not support the audio tag
      </audio>}

      {/* sidebar */}
      <div className='ui sidebar inverted vertical menu visible very thin'>
        <div className='item'><h2>W&M Robo Ops</h2></div>
        <div onClick={this.changeView.bind(this, CAMERAS)} className={`item ${this.state.view === CAMERAS ? 'active' : ''}`}>
          <div>Cameras</div>
        </div>
        <div onClick={this.changeView.bind(this, BIG_DADDY)} className={`item ${this.state.view === BIG_DADDY ? 'active' : ''}`}>
          {data.getIn([BIG_DADDY, 'batteryLevel']) < data.get('minBattery') && <i className='icon warning red'></i>}
          Big Daddy
        </div>
        <div onClick={this.changeView.bind(this, SCOUT)} className={`item ${this.state.view === SCOUT ? 'active' : ''}`}>
          {data.getIn([SCOUT, 'batteryLevel']) < data.get('minBattery') && <i className='icon warning red'></i>}
          Scout
        </div>
        <div onClick={this.changeView.bind(this, FLYER)} className={`item ${this.state.view === FLYER ? 'active' : ''}`}>
          {data.getIn([FLYER, 'batteryLevel']) < data.get('minBattery') && <i className='icon warning red'></i>}
          Flyer
        </div>
        <div onClick={this.changeView.bind(this, SETTINGS)} className={`item ${this.state.view === SETTINGS ? 'active' : ''}`}>
          Settings
        </div>
      </div>


      <div className='pusher' style={{padding: '25px', marginLeft: '210px'}}>

        <div style={{marginBottom: '20px'}}>
          <h1 className='ui block header center'>{names[this.state.view]}</h1>
        </div>

        {this.state.view === CAMERAS && <CamerasView />}

        {this.state.view === SETTINGS && <SettingsView
          muted={data.get('muted')}
          mute={store.dispatch.bind(this, mute())}
          unmute={store.dispatch.bind(this, unmute())}
          setMinBattery={this.setMinBattery}
          minBattery={data.get('minBattery')}
        />}

        {((this.state.view !== CAMERAS) && (this.state.view !== SETTINGS)) && <div>

          <div className='ui grid'>

            <div className='three wide column'>
              {/* zoom control */}
              <div className='ui teal padded segment'>
                <h1 className='ui dividing header'>zoom</h1>
                <ZoomControl />
              </div>

              {/* pan control */}
              <div className='ui teal padded segment'>
                <h1 className='ui dividing header'>pan</h1>
                <PanControl />
              </div>
            </div>

            <div className='seven wide column'>
              <div className='ui teal padded segment'>
                <h1 className='ui dividing header'>camera</h1>
                <VideoPlayer />
              </div>
            </div>

            {/* location */}
            <div className='six wide column'>
              <div className='ui black padded segment'>
                <h1 className='ui dividing header'>location</h1>
                <MainMap vehicles={vehicleLocations} rockData={rockData} removeRock={this.removeRock}/>
              </div>
            </div>

            {/* rock form */}
            <div className='five wide column'>
              <div className='ui red padded segment'>
                <h1 className='ui dividing header'>add rock</h1>
                <RockCoordinatesForm submit={this.addRock} vehicleLocations={vehicleLocations} colors={colors}/>
              </div>
            </div>

            {/* battery level */}
            <div className='five wide column'>
              <div className='ui pink padded segment'>
                <h1 className='ui dividing header'>battery</h1>
                <Battery batteryLevel={batteryLevel}/>
                <BatterySparkline level={batteryLevelHistory}/>
              </div>
            </div>

            {/* bearing-pitch-roll visualization */}
            <div className='six wide column'>
              <div className='ui red padded segment'>
                <h1 className='ui dividing header'>bearing, pitch, roll</h1>
                <BearingPitchRollVisualization x={pitch.get(0)} y={pitch.get(1)} z={pitch.get(2)} />
              </div>
            </div>

            {/* rock list */}
            <div className='six wide column'>
              <div className='ui red padded segment'>
                <h1 className='ui dividing header'>Rocks</h1>
                <RockList rocks={rockData} removeRock={this.removeRock} />
              </div>
            </div>

            {/* network and quality*/}
            <div className='five wide column'>
              <div className='ui purple padded segment'>
                <h1 className='ui dividing header'>network</h1>
                <NetworkSparkline speed={networkSpeed}/>
              </div>
            </div>

            {/* bearing map */}
            <div className='five wide column'>
              <div className='ui red padded segment'>
                <h1 className='ui dividing header'>bearing</h1>
                {/*<BearingMap bearing={bearing} center={loc} markerColor={'#ff00ff'}/>*/}
              </div>
            </div>


          </div>

        </div>}

      </div>

    </div>;
  }

}

export const MUTE = 'MUTE';
export const UNMUTE = 'UNMUTE';
export const ADD_ROCK = 'ADD_ROCK';
export const SET_ROCKS = 'SET_ROCKS';
export const REMOVE_ROCK = 'REMOVE_ROCK';
export const PITCH_UPDATE = 'PITCH_UPDATE';
export const BATTERY_UPDATE = 'BATTERY_UPDATE';
export const UPDATE_BEARING = 'UPDATE_BEARING';
export const LOCATION_UPDATE = 'LOCATION_UPDATE';
export const SET_MIN_BATTERY = 'SET_MIN_BATTERY';
export const NETWORK_SPEED_UPDATE = 'NETWORK_SPEED_UPDATE';

export function updateBattery(data) {
  let { vehicle, batteryLevel } = data;
  return { type: BATTERY_UPDATE, vehicle, batteryLevel };
}

export function updateLocation(data) {
  let { vehicle, location } = data;
  return { type: LOCATION_UPDATE, vehicle, location };
}

export function updateNetworkSpeed(data) {
  return Object.assign({ type: NETWORK_SPEED_UPDATE }, data);
}

export function updateBearing(data) {
  let { vehicle, bearing } = data;
  return { type: UPDATE_BEARING, vehicle, bearing };
}

export function updatePitch(data) {
  let { vehicle, pitch } = data;
  return { type: PITCH_UPDATE, vehicle, pitch };
}

export function addRock(data) {
  return Object.assign({ type: ADD_ROCK }, data);
}

export function setRocks(rocks) {
  return { type: SET_ROCKS, rocks };
}

export function removeRock(id) {
  return { type: REMOVE_ROCK, id };
}

export function mute() {
  return { type: MUTE };
}

export function unmute() {
  return { type: UNMUTE };
}

export function setMinBattery(min) {
  return { type: SET_MIN_BATTERY, min };
}

import Immutable from 'immutable';
import {
  BATTERY_UPDATE,
  LOCATION_UPDATE,
  NETWORK_SPEED_UPDATE,
  UPDATE_BEARING,
  PITCH_UPDATE,
  ADD_ROCK,
  REMOVE_ROCK,
  SET_ROCKS,
  MUTE,
  UNMUTE,
  SET_MIN_BATTERY
} from './actions';

function dashboardApp(state, action) {
  let { vehicle } = action;
  switch (action.type) {
    case BATTERY_UPDATE:
      state = state.setIn([vehicle, 'batteryLevel'], action.batteryLevel);
      return state.setIn([vehicle, 'batteryLevelHistory'], state.getIn([vehicle, 'batteryLevelHistory']).push(action.batteryLevel));
    case LOCATION_UPDATE:
      return state.setIn([vehicle, 'location'], Immutable.List(action.location)); // eslint-disable-line new-cap
    case NETWORK_SPEED_UPDATE:
      let s = state.setIn([vehicle, 'networkSpeed'], state.getIn([vehicle, 'networkSpeed']).push(Immutable.fromJS(action.data)));
      if (s.getIn([vehicle, 'networkSpeed']).size > 10) {
        s = s.setIn([vehicle, 'networkSpeed'], s.getIn([vehicle, 'networkSpeed']).shift()); // limit to 6 max in the array of data
      }
      return s;
    case UPDATE_BEARING:
      return state.setIn([vehicle, 'bearing'], action.bearing);
    case PITCH_UPDATE:
      return state.setIn([vehicle, 'pitch'], Immutable.List(action.pitch)); // eslint-disable-line new-cap
    case ADD_ROCK:
      let data = {
        lat: action.lat,
        lon: action.lon,
        color: action.color,
        id: action.id
      };
      return state.set('rocks', state.get('rocks').push(Immutable.fromJS(data))); // eslint-disable-line new-cap
    case REMOVE_ROCK:
      return state.set('rocks', state.get('rocks').filter(r => r.get('id') !== action.id));
    case SET_ROCKS:
      return state.set('rocks', Immutable.fromJS(action.rocks));
    case MUTE:
      return state.set('muted', true);
    case UNMUTE:
      return state.set('muted', false);
    case SET_MIN_BATTERY:
      return state.set('minBattery', action.min);
  }
  return state;
}

export default dashboardApp;

const URL = 'http://localhost:5555';

export function getStats() {
  return new Promise((resolve, reject) => {
    fetch(`${URL}/stats`)
      .then(res => res.json())
      .then(data => resolve(data))
      .catch(e => reject(e));
  });
}

export function getRocks() {
  return new Promise((resolve, reject) => {
    fetch(`${URL}/rocks`)
      .then(res => res.json())
      .then(data => resolve(data))
      .catch(e => reject(e));
  });
}

export function postRock(data) {
  return new Promise((resolve, reject) => {
    fetch(`${URL}/rocks/add`, {
      method: 'POST',
      headers: new Headers({ 'Content-Type': 'application/json' }),
      body: JSON.stringify(data)
    })
      .then(res => res.text())
      .then(text => {
        if (text !== 'ok') reject('DELETE failed');
      })
      .catch(e => reject(e));
  });
}

export function deleteRock(id) {
  return new Promise((resolve, reject) => {
    fetch(`${URL}/rocks/remove/${id}`, {
      method: 'DELETE'
    })
      .then(res => res.text())
      .then(text => {
        if (text !== 'ok') reject('DELETE failed');
      })
      .catch(e => reject(e));
  });
}

export function getBatteryLevel(/*vehicle*/) {
  return random(0, 100, true);
}

export function getLocation(/*vehicle*/) {
  var center = [-95.081320, 29.564835];
  return [
    random(center[0] - 0.0004, center[0] + 0.0004),
    random(center[1] - 0.0004, center[1] + 0.0004)
  ];
}

export function getNetworkSpeed(/*vehicle*/) {
  return random(0, 10, true);
}

export function getBearing() {
  return random(0, 179, true);
}

export function getPitch() {
  return [random(0, 2 * Math.PI), random(0, 2 * Math.PI), random(0, 2 * Math.PI)];
}

function random(min, max, integer) {
  var d = max - min;
  var n = Math.random() * d + min;
  n = integer ? Math.floor(n) + 1 : n;
  return n;
}

